INSERT INTO Skillset (skillset_name ,skillset_wiki_description ,skillset_description ,skillset_wiki_link ,skillset_image) VALUES ('Web app, Back-end' , 'A web application or web app is any software that runs in a web browser. It is created in a browser-supported programming language (such as the combination of JavaScript, HTML and CSS) and relies on a web browser to render the application.' , 'Spring, J2EE, Hibernate' , 'http://en.wikipedia.org/wiki/Front_and_back_ends' , 'static/images/skillset_icon/web_app_backend_icon.png');
INSERT INTO Skillset (skillset_name ,skillset_wiki_description ,skillset_description ,skillset_wiki_link ,skillset_image) VALUES ('Mobile computing' , 'Mobile computing is human computer interaction by which a computer is expected to be transported during normal usage. Mobile computing involves mobile communication, mobile hardware, and mobile software. Communication issues include ad hoc and infrastructure networks as well as communication properties, protocols, data formats and concrete technologies. Hardware includes mobile devices or device components. Mobile software deals with the characteristics and requirements of mobile applications.' , 'Mobile computing, IOS, Andriod' , 'http://en.wikipedia.org/wiki/Mobile_computing' , 'static/images/skillset_icon/mobile_computing_icon.png');
INSERT INTO Skillset (skillset_name ,skillset_wiki_description ,skillset_description ,skillset_wiki_link ,skillset_image) VALUES ('Network' , 'A computer network or data network is a telecommunications network which allows computers to exchange data. In computer networks, networked computing devices pass data to each other along data connections (network links). Data is transferred in the form of packets. The connections between nodes are established using either cable media or wireless media. The best-known computer network is the Internet.' , 'Network administration, Cloud computing' , 'http://en.wikipedia.org/wiki/Computer_network' , 'static/images/skillset_icon/network_icon.png');
INSERT INTO Skillset (skillset_name ,skillset_wiki_description ,skillset_description ,skillset_wiki_link ,skillset_image) VALUES ('Web Front-end' , 'In software engineering, the terms front end and back end are distinctions which refer to the separation of concerns between a presentation layer and a data access layer respectively. The front end is an interface between the user and the back end. The front and back ends may be distributed amongst one or more systems.' , 'Html, javascript, CSS' , 'http://en.wikipedia.org/wiki/Front_and_back_ends' , 'static/images/skillset_icon/web_frontend_icon.png');
INSERT INTO Skillset (skillset_name ,skillset_wiki_description ,skillset_description ,skillset_wiki_link ,skillset_image) VALUES ('Big data' , 'Big data is a broad term for data sets so large or complex that traditional data processing applications are inadequate. Challenges include analysis, capture, curation, search, sharing, storage, transfer, visualization, and information privacy.' , 'Hadoop, big data' , 'http://en.wikipedia.org/wiki/Big_data' , 'static/images/skillset_icon/bigdata_icon.png');
INSERT INTO Skillset (skillset_name ,skillset_wiki_description ,skillset_description ,skillset_wiki_link ,skillset_image) VALUES ('Database' , 'A database is an organized collection of data. The data is typically organized to model aspects of reality in a way that supports processes requiring information. For example, modelling the availability of rooms in hotels in a way that supports finding a hotel with vacancies.' , 'MySQL, NoSQL, MongoDB' , 'http://en.wikipedia.org/wiki/Database' , 'static/images/skillset_icon/database_icon.png');
